import React, { Component } from 'react';
import './App.css';
import { Welcome, PersonChoose, CarInfo, Driver_Start, Driver_Finish, Rider_Start, Rider_Finish, Confirm } from './components/Steps.js';
import { states } from './components/States.js';
import { StateMachine } from './components/StateMachine.js';

const styles = {
  card: {
    minWidth: 275,
  },
  title: {
    fontSize: 30,
  },
  pos: {
    marginBottom: 12,
  },
};

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentState: states.WELCOME,
      vehicleType: null,
      vehicles: []
    };
    this._next = this._next.bind(this);
    this._back = this._back.bind(this);
    this._saveVehicle = this._saveVehicle.bind(this);
    this.stateMachine = new StateMachine();
  }

  _saveVehicle(vehicle) {
    let vehicles = this.state.vehicles.concat();
    vehicles.push(vehicle);
    this.setState({
      vehicles: vehicles
    });
  }

  _next(desiredState) {
    let currentState = this.state.currentState;
    let nextState = this.stateMachine.transitionTo(currentState, desiredState);
    this.setState({
      currentState: nextState
    });
  }

  _back(desiredState) {
    let currentState = this.state.currentState;
    this.setState({
      currentState: this.stateMachine.transitionFrom(currentState, desiredState)
    });
  }

  /*
   * Just a note -- you'll see the _next and _back functions
   * get passed around to child components alot. This is not
   * a very good practice, and in the real-world it would be
   * better to use a library like redux to handle application
   * state.
   */
  _currentStep() {
    switch (this.state.currentState) {
      case states.WELCOME:
        return (<Welcome next={this._next} />);
      case states.VEHICLE_CHOOSE:
        return (<VehicleChoose
          back={this._back}
          next={this._next} />);
      case states.CAR:
        return (<CarForm
          saveForm={this._saveVehicle}
          back={this._back}
          next={this._next} />);
      case states.BOAT:
        return (<BoatForm
          saveForm={this._saveVehicle}
          back={this._back}
          next={this._next} />);
      case states.BOAT_DETAIL:
        return (<BoatDetail
          back={this._back}
          next={this._next} />);
      case states.CONFIRM:
        return (<Confirm
          vehicles={this.state.vehicles}
          back={this._back}
          next={this._next} />);
      default:
        return (<Welcome next={this._next} />);
    }
  }
  render() {
    return (
      <div className="App">
        <div>
          {this._currentStep()}
        </div>
      </div>
    );
  }
}

export default App;
